    public static int totalStrength_02(int[] strength) {
        long[] pre = new long[strength.length + 1], pre2 = new long[strength.length + 2];
        long sum = 0, cur = 0, m = 0;

        for (int i = 0; i < strength.length; i++) {
            pre2[i + 2] = (pre2[i + 1] + (pre[i + 1] = (pre[i] + strength[i]) % mod)) % mod;
        }

        PriorityQueue<Integer> queue = new PriorityQueue<>((o, p) -> strength[p] - strength[o]);
        for (int i = 0; i < strength.length; queue.offer(i++)) {
            while (!queue.isEmpty() && strength[queue.peek()] >= strength[i]) {
                int j = queue.poll(), k = queue.isEmpty() ? -1 : queue.peek();
                m = ((m + (strength[i] - strength[j]) * ((long) j - k)) % mod + mod) % mod;
                cur = ((cur + (strength[i] - strength[j]) * (pre[i] * (j - k) - pre2[j + 1] + pre2[k + 1])) % mod
                        + mod) % mod;
            }
            sum = (sum + (cur = (cur + (m = (m + strength[i]) % mod) * strength[i]) % mod)) % mod;
        }
        return (int) sum;
    }
